// Helper function to get CSS variable value
export function getCSSVariable(variableName: string): string {
  if (typeof window === 'undefined') return ''
  return getComputedStyle(document.documentElement).getPropertyValue(variableName).trim()
}

// Helper function to set CSS variable value
export function setCSSVariable(variableName: string, value: string): void {
  if (typeof window === 'undefined') return
  document.documentElement.style.setProperty(variableName, value)
}

// Role color helper
export function getRoleColor(role: keyof typeof tokens.colors.role): string {
  return tokens.colors.role[role]
}

// Access level color helper
export function getAccessLevelColor(level: 1 | 2 | 3 | 4 | 5): string {
  return tokens.colors.access[level]
}

// Semantic color helper
export function getSemanticColor(
  type: 'success' | 'warning' | 'danger' | 'info',
  shade: keyof typeof tokens.colors.success = 500
): string {
  return tokens.colors[type][shade]
}

